                + "AND e.est_estado = 1 "
                + "GROUP BY e.EST_CODIGO, e.EST_COD_MATRICULA, e.EST_NOMBRES, e.EST_APELLIDOS "
                + "ORDER BY e.EST_CODIGO"; 
        Reporte.Logo="images/logo.png";
        Reporte.ncol=3;
